BEGIN
  SayingHi.HelloWorld;
END HelloWorld2.

MODULE SayingHi;
  IMPORT In, Out;

  PROCEDURE HelloWorld*;
  BEGIN
    Out.String("Hello World!"); Out.Ln;
  END HelloWorld;

  PROCEDURE AskOurName;
  BEGIN
    Out.String("Excuse me, may I ask your name? ");
  END AskOurName;

  PROCEDURE GetName(VAR ourName : ARRAY OF CHAR);
  BEGIN
    In.Line(ourName);
  END GetName;

  PROCEDURE AssembleGreeting(ourName : ARRAY OF CHAR);
  BEGIN
    Out.String("Hello ");Out.String(ourName);
    Out.String (", very nice to meeting you."); Out.Ln;
  END AssembleGreeting;

  PROCEDURE Greetings*;
    VAR ourName : ARRAY 256 OF CHAR;
  BEGIN
    AskOurName;
    GetName(ourName);
    AssembleGreeting(ourName);
  END Greetings;
END SayingHi.

MODULE HelloWorld2;
  IMPORT SayingHi;
BEGIN
  SayingHi.HelloWorld;
  SayingHi.Greetings;
END HelloWorld2.

PROCEDURE HelloWorld*;
BEGIN
  Out.String("Hello World!"); Out.Ln;
END HelloWorld;

PROCEDURE AskOurName;
BEGIN
  Out.String("Excuse me, may I ask your name? ");
END AskOurName;

PROCEDURE AssembleGreeting(ourName : ARRAY OF CHAR);
BEGIN
  Out.String("Hello ");Out.String(ourName);
  Out.String (", very nice to meeting you."); Out.Ln;
END AssembleGreeting;
